# round() is built into template strings! (f"")
pi = 3.
precision = 100
# print(f"Pi rounded to {precision} decimals = {pi:.precisionf}") sad this doesn't work (must use round() explicitly)
print(f"Pi rounded to 6 decimals = {pi:.6f}")
print(f"Pi rounded to {precision} decimals = {round(pi, precision)}")
# or this works as well
print(f"Pi rounded to {precision} decimals = {pi:.{precision}f}")

# BMI (Body Mass Index); getting 18.5-25 is normal
weight = float(input("Weight (kg): "))
height = float(input("Height (m): "))
bmi = weight / (height ** 2)
print(f"Your BMI is {round(bmi, 1)}")